params.query:
-- Create games table
CREATE TABLE IF NOT EXISTS games (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  host_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  name TEXT NOT NULL,
  description TEXT,
  max_players INTEGER DEFAULT 8 CHECK (max_players >= 2 AND max_players <= 8),
  current_players INTEGER DEFAULT 1 CHECK (current_players >= 0),
  status TEXT DEFAULT 'open' CHECK (status IN ('open', 'in_progress', 'completed', 'cancelled')),
  invite_code TEXT UNIQUE NOT NULL,
  scheduled_date TIMESTAMP WITH TIME ZONE,
  location TEXT
);

-- Create game_members table
CREATE TABLE IF NOT EXISTS game_members (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  game_id UUID REFERENCES games(id) ON DELETE CASCADE NOT NULL,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  joined_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  UNIQUE(game_id, user_id)
);

-- Create chat_messages table
CREATE TABLE IF NOT EXISTS chat_messages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  game_id UUID REFERENCES games(id) ON DELETE CASCADE NOT NULL,
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE NOT NULL,
  message TEXT NOT NULL,
  user_name TEXT
);

-- Enable RLS
ALTER TABLE games ENABLE ROW LEVEL SECURITY;
ALTER TABLE game_members ENABLE ROW LEVEL SECURITY;
ALTER TABLE chat_messages ENABLE ROW LEVEL SECURITY;

-- RLS Policies for games
CREATE POLICY "Anyone can view open games" ON games
  FOR SELECT USING (status = 'open' OR id IN (
    SELECT game_id FROM game_members WHERE user_id = auth.uid()
  ));

CREATE POLICY "Authenticated users can create games" ON games
  FOR INSERT TO authenticated
  WITH CHECK (auth.uid() = host_id);

CREATE POLICY "Host can update their games" ON games
  FOR UPDATE TO authenticated
  USING (auth.uid() = host_id);

CREATE POLICY "Host can delete their games" ON games
  FOR DELETE TO authenticated
  USING (auth.uid() = host_id);

-- RLS Policies for game_members
CREATE POLICY "Game members can view members" ON game_members
  FOR SELECT USING (
    game_id IN (SELECT game_id FROM game_members WHERE user_id = auth.uid())
  );

CREATE POLICY "Authenticated users can join games" ON game_members
  FOR INSERT TO authenticated
  WITH CHECK (auth.uid() = user_id);

CREATE POLICY "Users can leave games" ON game_members
  FOR DELETE TO authenticated
  USING (auth.uid() = user_id);

-- RLS Policies for chat_messages
CREATE POLICY "Game members can view messages" ON chat_messages
  FOR SELECT USING (
    game_id IN (SELECT game_id FROM game_members WHERE user_id = auth.uid())
  );

CREATE POLICY "Game members can send messages" ON chat_messages
  FOR INSERT TO authenticated
  WITH CHECK (
    auth.uid() = user_id AND
    game_id IN (SELECT game_id FROM game_members WHERE user_id = auth.uid())
  );

-- Create indexes for performance
CREATE INDEX idx_games_status ON games(status);
CREATE INDEX idx_games_invite_code ON games(invite_code);
CREATE INDEX idx_game_members_game_id ON game_members(game_id);
CREATE INDEX idx_game_members_user_id ON game_members(user_id);
CREATE INDEX idx_chat_messages_game_id ON chat_messages(game_id);
CREATE INDEX idx_chat_messages_created_at ON chat_messages(created_at);

-- Create function to update game player count
CREATE OR REPLACE FUNCTION update_game_player_count()
RETURNS TRIGGER AS $$
BEGIN
  IF TG_OP = 'INSERT' THEN
    UPDATE games 
    SET current_players = current_players + 1,
        updated_at = NOW()
    WHERE id = NEW.game_id;
  ELSIF TG_OP = 'DELETE' THEN
    UPDATE games 
    SET current_players = current_players - 1,
        updated_at = NOW()
    WHERE id = OLD.game_id;
  END IF;
  RETURN COALESCE(NEW, OLD);
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create trigger for player count
CREATE TRIGGER update_player_count_trigger
  AFTER INSERT OR DELETE ON game_members
  FOR EACH ROW
  EXECUTE FUNCTION update_game_player_count();

-- Create function to broadcast chat messages
CREATE OR REPLACE FUNCTION broadcast_chat_message()
RETURNS TRIGGER AS $$
BEGIN
  PERFORM realtime.broadcast_changes(
    'game:' || NEW.game_id::text || ':chat',
    TG_OP,
    TG_OP,
    TG_TABLE_NAME,
    TG_TABLE_SCHEMA,
    NEW,
    OLD
  );
  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create trigger for chat broadcasts
CREATE TRIGGER chat_message_broadcast_trigger
  AFTER INSERT ON chat_messages
  FOR EACH ROW
  EXECUTE FUNCTION broadcast_chat_message();

-- RLS policy for realtime.messages (for private channels)
CREATE POLICY "game_members_can_receive_broadcasts" ON realtime.messages
  FOR SELECT TO authenticated
  USING (
    topic LIKE 'game:%:chat' AND
    EXISTS (
      SELECT 1 FROM game_members
      WHERE user_id = auth.uid()
      AND game_id = SPLIT_PART(topic, ':', 2)::uuid
    )
  );

CREATE POLICY "game_members_can_send_broadcasts" ON realtime.messages
  FOR INSERT TO authenticated
  WITH CHECK (
    topic LIKE 'game:%:chat' AND
    EXISTS (
      SELECT 1 FROM game_members
      WHERE user_id = auth.uid()
      AND game_id = SPLIT_PART(topic, ':', 2)::uuid
    )
  );
